mapper.select:
select a.article_id, a.title, a.content, a.reg_date, a.mod_date, a.view_count, a.map_json, a.category, a.user_id, a.party_id,a.delete_yn, count(l.likes_id) as likesCount, u.username from article a left join article_likes l ON a.article_id = l.target_id left join user u ON u.user_id = a.user_id group by a.article_id having category = #{category} and party_id = #{partyId} and delete_yn = 'N'
and ${condition.key} like concat('%',#{condition.word},'%')
order by ${condition.orderBy} ${condition.orderByDir}

mapper.update:
update article set
title = #{title}, content = #{content}, mod_date = now(), user_id = #{userId}, party_id =#{partyId}, map_json = #{mapJson}
where article_id = #{articleId}
