mapper.select:
select article_id, title, content, reg_date, mod_date, view_count, category, user_id, party_id from article where category = #{category} and party_id = #{partyId}
and ${condition.key} like concat('%',#{condition.word},'%')
order by ${condition.orderBy} ${condition.orderByDir}

mapper.insert:
update article set
title = #{title}, content = #{content}, mod_date = now(), user_id = #{userId}, party_id =#{partyId}
where article_id = #{articleId}
